/**
 * Get placeholder image by type
 */
function taoism_get_placeholder($type = 'default') {
    $images = array(
        'hero' => TAOISM_HERO_IMAGE_1,
        'culture-1' => TAOISM_CULTURE_IMAGE_1,
        'culture-2' => TAOISM_CULTURE_IMAGE_2,
        'culture-3' => TAOISM_CULTURE_IMAGE_3,
        'culture-4' => TAOISM_CULTURE_IMAGE_4,
        'product-tea' => TAOISM_PRODUCT_TEA_SET,
        'product-cushion' => TAOISM_PRODUCT_CUSHION,
        'product-scroll' => TAOISM_PRODUCT_SCROLL,
        'product-incense' => TAOISM_PRODUCT_INCENSE,
        'article-1' => TAOISM_ARTICLE_1,
        'article-2' => TAOISM_ARTICLE_2,
        'article-3' => TAOISM_ARTICLE_3,
        'article-4' => TAOISM_ARTICLE_4,
        'article-5' => TAOISM_ARTICLE_5,
        'article-6' => TAOISM_ARTICLE_6,
        'avatar' => TAOISM_AVATAR_PLACEHOLDER,
        'default' => TAOISM_HERO_IMAGE_1,
    );
    
    return isset($images[$type]) ? $images[$type] : $images['default'];
}

